    function get_CURL($url) {
        $curl = curl_init();
        curl_setopt($curl, CURLOPT_URL, $url);
        curl_setopt($curl, CURLOPT_RETURNTRANSFER, 1);
        $result = curl_exec($curl);
        curl_close($curl);
    
        return json_decode($result, true);
    }

    $indonesia = get_CURL('https://api.kawalcorona.com/indonesia/');
